function bxSliderInit() {
  $(".bxslider").bxSlider({
    mode: "fade",
    auto: true,
    autoControls: true,
    pause: 5000,
    speed: 1300,
  });
}

function addPageTopHandler() {
  $("#pageTop").click(function () {
    $("body,html").animate(
      {
        scrollTop: 0,
      },
      500
    );
    return false;
  });
}

function addMoveToNewsHandler() {
  $("#move").click(function () {
    var speed = 400;
    var href = $(this).attr("href");
    var target = $(href == "#" || href == "" ? "html" : href);
    var position = target.offset().top;
    $("body,html").animate({ scrollTop: position }, speed, "swing");
    return false;
  });
}
